		allCards: [],
    gameCards: [],
    selectedCards: [],
  }

  componentDidMount() {
		axios.get('http://localhost:5000')
		.then(res => this.setState({allCards: res.data}))
		.then(cards => console.log("cards", cards))
  }

	spliceAllCards = () => {
		const { allCards } = this.state;
		const game = allCards.splice(0,12)
	}

  recordValue = (e) => {
		const { selectedCards } = this.state;
    this.state.selectedCards.push(e.target.value);
    this.setState({selectedCards})
  }

  render() {
		const { allCards, selectedCards, gameCards} = this.state;
		// const game = (allCards.splice(0,12))
		console.log("allCards", allCards)
		// console.log("solu", gameCards)
		console.log("game", this.game)
		console.log("selectedCards", selectedCards)
    return (
			this.game.map((card)=>{ return(
      <div>
				{card.code} <button value={card.code} name='cps3' onClick={this.recordValue}>Click to record value</button>
      </div>
			)})
    )
  }
}

export default GamePage;
